mapper.delete:
delete from device
where device_id in

    #{id}

mapper.select:
select device_id, device_name, device.device_type_id, device_type_name, device_status_id, device_status, device_purchase_date, device_purchase_price, device_manufacture_date, device_service_life, device.note,
employee.emp_id, employee.emp_name
from device left join device_type on device.device_type_id = device_type.device_type_id left join employee on device.device_keeper_id = employee.emp_id where device_id = #{id}
